import React from 'react';
import styled from "styled-components";
import LogoImg from "../../EM.jpg"

const brandLogoContainer = styled.div `
display: flex;
align-items: center;
`;

const LogoImage =  styled.div `
align:center;
width: ${({size}) => (size ? size + "px" : ".25em")};
height: ${({size}) => (size ? size + "px" : ".25em")};
img {
    width: 100%;
    height: 100%;
}
`;

const LogoTitle = styled.h2 `
margin: 0;
font-size: ${({size}) => (size ? size + "px" : ".25em")};
color: #fff;
font-weight: 500;
// font-size: 100px;
`;

export default function brandLogo(props) {
    const {logoSize, textsize} = props;
return (
    <brandLogoContainer>
        <LogoImage size={logoSize}>
            <img src={LogoImg} alt="Human Resources Logo" />
        </LogoImage>
        <LogoTitle size={textsize}>Employee Management</LogoTitle>
    </brandLogoContainer>
);
}
