import React from 'react';
import styled from "styled-components";
import LogoImg from "../../HR_logo.png"

const brandLogoContainer = styled.div `
display: flex;
align-items: center;
`;

const LogoImage =  styled.div `
width: 100%;
height: 100%;
img {
    width: 1em;
    height: 1em;
}
`;

const LogoTitle = styled.h2 `
margin: 0;
width: ${({size}) => size ? size + "px" : "60px"};
color: #fff;
font-weight: 500;
// font-size: 60px;
`;

export default function brandLogo(props) {
    const {size} = props;
return (
    <brandLogoContainer>
        <LogoImage size={size}>
            <img src={LogoImg} alt="Human Resources Logo" />
        </LogoImage>
        <LogoTitle size={size}>Human Resources!</LogoTitle>
    </brandLogoContainer>
);
}
