import React from 'react';
import styled from 'styled-components';
import LogoImg from "../../EM.jpg";

const NavbarContainer = styled.div`
// margin-top:-.2em;
width: 100%;
height: 0px;
display: flex;
align-items center;
padding: 0 20px;
`;

const brandLogoContainer = styled.div `
display: flex;
align-items: center;
`;

const LogoImage =  styled.div `
float: left;
width: ${({size}) => (size ? size + "px" : ".08em")};
height: ${({size}) => (size ? size + "px" : ".08em")};
img {
    border-radius: 50px;
    width: 100%;
    height: 100%;
}
`;

const LogoTitle = styled.h2 `
display: inline;
white-space: nowrap;
margin: 0;
font-size: ${({size}) => (size ? size + "px" : ".1em")};
color: #fff;
font-weight: 500;
margin-left: 10px;
`;

const AccessibilityContainer = styled.div `
display: flex;

`;

const AnchorLink = styled.a `
font-size: 17px;
color: #fff;
cursor: pointer;
text-decoration: none;
outline: none;
transition: all 200 ms ease-in-out;

&:hover {
    filter:contrast(0.6);
}
`;

export default function navbar(props) {

    const {logoSize, textsize} = props;

    return (
        <NavbarContainer>
            <brandLogoContainer>
        <LogoImage size={logoSize} className="bordered">
            <img src={LogoImg} alt="Human Resources Logo" />
        </LogoImage>
        <LogoTitle size={textsize}>Employee Management</LogoTitle>
    </brandLogoContainer>
    <AccessibilityContainer>
        <AnchorLink>Specialists Portal</AnchorLink>
    </AccessibilityContainer>
        </NavbarContainer>
    );
}
