import React from 'react';
import styled from "styled-components";
import brandLogo from '../../components/brandLogo';
import hrimage from "../../homepage.jpeg";
import bestHR from "../../human_resources_inner.jpg";

const TopSectionContainer = styled.div`
width: 100%;
height: 1750px;
background: url(${hrimage});
background-size: cover;
`;

const BackgroundFilter = styled.div`
width: 100%;
height: 100%;
background-color: rgba(35, 70, 83, 0.6);
display: flex;
flex-direction: column;
`;

const TopSectionInnerContainer = styled.div`
width: 100%;
height: 100%;
display: flex;
align-items: center;
justify-content: space-evenly;
`;

const StandoutImage = styled.div`
 width: 2em;
 height: 2em;
img{
    width: 100%;
    height: 100%;
}
`;

const LogoContainer = styled.div `
display: flex;
flex-direction: column;
`;

// const Title = styled.h2 `
// margin: 0;
// font-size: 100px;
// color: #fff;
// line-height: 1.7;
// `;

export default function topSection(props) {
    return (
    <TopSectionContainer>
        <BackgroundFilter>
            <LogoContainer>
                <brandLogo />
            </LogoContainer>
            {/* <Title>The future to Human Resources!!</Title> */}
            <StandoutImage>
               <img src={bestHR} alt="Best in the field" />
            </StandoutImage>
        </BackgroundFilter>
    </TopSectionContainer>
);
}
