import React from 'react';
import styled from 'styled-components';
import LogoImg from "../../EM.jpg";
import Button from "../button"

const NavbarContainer = styled.div`
margin-top:-.2em;
width: 100%;
height: 0px;
display: flex;
align-items center;
padding: 0 20px;
justify-content: space-between; 
`;

const brandLogoContainer = styled.div `
display: flex;
align-items: center;
`;

const LogoImage =  styled.div `
float: left;
width: ${({size}) => (size ? size + "px" : ".06em")};
height: ${({size}) => (size ? size + "px" : ".06em")};
img {
    border-radius: 50px;
    width: 100%;
    height: 100%;
}
`;

const LogoTitle = styled.h2 `
display: inline;
white-space: nowrap;
margin: 0;
font-size: ${({size}) => (size ? size + "px" : ".08em")};
color: #fff;
font-weight: 500;
margin-left: 10px;
`;

const AccessibilityContainer = styled.div `
display: flex;
align-items:center;
`;

const AnchorLink = styled.a `
margin-top:7.5em;
font-size: .08em;
color: #fff;
cursor: pointer;
text-decoration: none;
outline: none;
transition: all 200 ms ease-in-out;

&:hover {
    filter:contrast(0.6);
}
`;

const ButtonWrapper = styled.button`
width: 25%;
border: none;
outline: none;
color: #fff;
padding: 25px .3em;
margin-top:7.5em;
font-size: .08em;
font-weight: 250;
border-radius: 12.5px;
background-color:#2a2d7f;
text-align:center;
background-size: relative;
cursor: pointer;
transition: all 200 ms ease-in-out;

&: hover {
    background-color:#2a3d8f;
}

&:focus {
    outline: none;

}
`;


export default function navbar(props) {

    const {logoSize, textsize} = props;

    return (
        <NavbarContainer>
            <brandLogoContainer>
        <LogoImage size={logoSize} className="bordered">
            <img src={LogoImg} alt="Human Resources Logo" />
        </LogoImage>
        <LogoTitle size={textsize}>Employee Management</LogoTitle>
    </brandLogoContainer>
    <AccessibilityContainer>
        <AnchorLink>Specialists Portal</AnchorLink>
        <ButtonWrapper>Register</ButtonWrapper>
    </AccessibilityContainer>
        </NavbarContainer>
    );
}
