import React from 'react';
import styled from "styled-components";
import LogoImg from "../../HR_logo.png"

const brandLogoContainer = styled.div `
display: flex;
align-items: center;
`;

const LogoImage =  styled.div `
width: ${({size}) => size ? size + "px" : "150px"};
height: ${({size}) => size ? size + "px" : "150px"};
img {
    width: 100%;
    height: 100%;
}
`;

const LogoTitle = styled.h2 `
margin: 0;
width: ${({size}) => size ? size + "px" : "35px"};
color: #fff;
font-weight: 500;
font-size: 100px;
`;

export default function brandLogo(props) {
    const {logoSize, textsize} = props;
return (
    <brandLogoContainer>
        <LogoImage size={logoSize}>
            <img src={LogoImg} alt="Human Resources Logo" />

        <LogoTitle size={textsize}>Employee Management</LogoTitle>
        </LogoImage>
    </brandLogoContainer>
);
}
