import React from 'react'
import styled from 'styled-components';

const ServicesContainer = styled.div`
margin-top: 5em;
width: 100%;
display: flex;
flex-direction: column;
align-items: flex-start;
`;

const Title = styled.h2`
  font-size: 5em;
  margin-left: 4.5em;
  font-weight: 500;
  color: #000;
  text-align: flex-start;
  font-weight: 900;
`;

const ServicesWrapper = styled.div`
display: flex;
flex-wrap: wrap;
`;

export default function Services(props) {

    const fetchServices = async () => {
        cconst [offeredServices, setServices] = useState([]);
        const [isLoading, setLoading] = useState(false);

        const isServicesEmpty =
          !offeredServices || (offeredServices && offeredServices.length === 0);

        const fetchServices = async () => {
          setLoading(true);
          const response = await Axios.get("http://localhost:9000/services").catch(
            (err) => {
              console.log("Error: ", err);
            }
          );

          if (response) {
            setServices(response.data);
          }

          setLoading(false);
        };

        useEffect(() => {
          fetchServices();
        }, []);
    }

        return (
            <ServicesContainer>
              <Title>SOME OF OUR PERKS:</Title>
              <ServicesWrapper>
                {isServicesEmpty && !isLoading && (
                  <WarningText>No Services are published yet!</WarningText>
                )}
                {isLoading && <WarningText>Loading...</WarningText>}
                {!isServicesEmpty &&
                  !isLoading &&
                  offeredServices.map((service, idx) => (
                    <ServiceCard key={idx} {...service} />
                  ))}
              </ServicesWrapper>
              <BottomContainer>
                {!isServicesEmpty && !isLoading && (
                  <ViewMoreButton>View More</ViewMoreButton>
                )}
              </BottomContainer>
            </ServicesContainer>
          );
        }
